<?PHP	class Model_Login extends Model_Database	{//use md5(string)				private $database;				public function __construct()		{//empty constructor			$database = Database::instance();		}				public function login($user, $pass)		{			//do SELECT on auth table and see if we get a result.			//if not, do nothing.			$session = Session::instance();						$query = DB::query(Database::SELECT, 'SELECT * FROM auth WHERE `username` = "'.$user.'" AND `password` = "'.md5($pass).'"');//->execute();			$result = $query->execute();			if(count($result->as_array()) > 0)			{//if there is a result							$session->set('loggedin', true);				$session->set('username', $user);			}					}				public function logout()		{			$session = Session::instance();			$sesdat = $session->as_array();			if(isset($sesdat['loggedin']))			{				$session->delete('loggedin');			}		}				public function register($user, $pass)		{			$query = DB::query(Database::SELECT, 'SELECT * FROM auth WHERE `username` = "'.$user.'"');//->execute();						$result = $query->execute();			if(count($result->as_array()) < 1)			{//if there is a result						$query = DB::query(Database::INSERT, 'INSERT INTO `auth` (`username`, `password`) VALUES ("'.$user.'", "'.md5($pass).'")')->execute();			}		}			}?>